package AbstractConcept;

public class loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i[] = new int[3];
		i[0] = 10;
		i[1] = 20;
		i[2] = 30;
		
		
		int k = i.length;
		System.out.println(k);
		
		
		for(int j = 0; j<k; j++) {
			System.out.println(i[j]);
		}
		

}
}